import React, { useState, useEffect } from "react";
import { IonContent, IonHeader, IonPage, IonRefresher, IonRefresherContent, IonTitle, IonToolbar } from "@ionic/react";
import { RefresherEventDetail } from "@ionic/core";
import "./Courses.css";
import CoursesList from "../components/CoursesList";
import Course from "../models/Course";
import axios from "axios";

const Courses: React.FC = () => {
  const coursesData: Course[] = [];
  const [coursess, setCourses]: [Course[], (courses: Course[]) => void] = useState(
    coursesData
  );

  const getCoursesData = async () => {
    return new Promise(async (resolve: (value: Course[]) => void, reject) => {
      const response = await axios.get("https://webd3000functions-apim.azure-api.net/webd3000functions/courses");

      if (response.status === 200) {
        resolve(response.data);
      } else {
        reject("Unable to access data");
      }
    });
  };

  useEffect(() => {
    (async () => {
      console.log("use effect");
      try {
        const data = await getCoursesData();
        setCourses(data);
      } catch (err) {
        //inform the user that a problem occurred (some kind of alert)
      }
    })();
  }, []);

  const doRefresh: (event: CustomEvent<RefresherEventDetail>) => void = async (event) => {
    console.log("do refresh");

    //stop the spinner
    try {
      const data = await getCoursesData();
      setCourses(data);
      event.detail.complete();
    } catch (err) {
      //inform the user that a problem occurred (some kind of alert)
    }
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Courses</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">Tab 2</IonTitle>
          </IonToolbar>
        </IonHeader>
        <ExploreContainer name="Tab 2 page" />
      </IonContent>
    </IonPage>
  );
};

export default Courses;
