import React, { useState, useEffect } from "react";
import { RouteComponentProps } from "react-router";
import {
  IonButtons,
  IonBackButton,
  IonContent,
  IonHeader,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { RefresherEventDetail } from "@ionic/core";
import "./CourseDetails.css";
import { PrerequisiteCoursesList, PrerequisitesForList } from "../components/CourseDetailsList";
import CourseDetail from "../models/CourseDetail";
import axios from "axios";

interface CourseDetailsProps
  extends RouteComponentProps<{
    id: string;
  }> {}

const CourseDetails: React.FC<CourseDetailsProps> = ({ match }) => {
  const courseDetailData: CourseDetail[] = [];
  const [prerequisiteCourses, setPrerequisiteCourses]: [
    Course[],
    (academicYearSemesterPrerequisiteCoursesData: Course[]) => void
  ] = useState(prerequisiteCoursesData);

  const getCourseDetailsData = async () => {
    return new Promise(async (resolve: (value: [Course[], Course[]]) => void, reject) => {
      const response = await axios.get(
        `https://webd3000functions-apim.azure-api.net/webd3000functions/course/${match.params.id}`
      );

      if (response.status === 200) {
        console.debug(response.data);

        resolve([response.data.prerequisiteCourses, response.data.prerequisitesFor]);
      } else {
        reject("Unable to access data");
      }
    });
  };

  useEffect(() => {
    (async () => {
      console.log("use effect");
      try {
        const data = await getCourseDetailsData();
        setPrerequisiteCourses(data[0]);
        setPrerequisitesFor(data[1]);
      } catch (err) {
        //inform the user that a problem occurred (some kind of alert)
      }
    })();
  }, []);

  const doRefresh: (event: CustomEvent<RefresherEventDetail>) => void = async (event) => {
    console.log("do refresh");

    //stop the spinner
    try {
      const data = await getCourseDetailsData();
      setPrerequisiteCourses(data[0]);
      setPrerequisitesFor(data[1]);
      event.detail.complete();
    } catch (err) {
      //inform the user that a problem occurred (some kind of alert)
    }
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="home" />
          </IonButtons>
          <IonTitle>Course Details</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={doRefresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>
        <IonToolbar>
          <IonTitle color="primary">Prerequsite Courses</IonTitle>
          <PrerequisiteCoursesList prerequisiteCourses={prerequisiteCourses} />
        </IonToolbar>
        <IonToolbar>
          <IonTitle color="primary">Prerequisites For</IonTitle>
          <PrerequisitesForList prerequisitesFor={prerequisitesFor} />
        </IonToolbar>
      </IonContent>
    </IonPage>
  );
};

export default CourseDetails;
