import React, { useState, useEffect } from "react";
import {
  IonContent,
  IonHeader,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  IonSearchbar,
} from "@ionic/react";
import { RefresherEventDetail } from "@ionic/core";
import "./Instructors.css";
import InstructorsList from "../components/InstructorsList";
import Instructor from "../models/Instructor";
import axios from "axios";

const Instructors: React.FC = () => {
  const InstructorsData: Instructor[] = [];
  const [courses, setCourses]: [Instructor[], (courses: Instructor[]) => void] = useState(coursesData);
  // for search bar
  const [searchText, setSearchText] = useState("");

  const getCoursesData = async () => {
    return new Promise(async (resolve: (value: Instructor[]) => void, reject) => {
      const response = await axios.get("https://webd3000functions-apim.azure-api.net/webd3000functions/courses");

      if (response.status === 200) {
        resolve(response.data);
      } else {
        reject("Unable to access data");
      }
    });
  };

  useEffect(() => {
    (async () => {
      console.log("use effect");
      try {
        const data = await getCoursesData();
        setCourses(data);
      } catch (err) {
        //inform the user that a problem occurred (some kind of alert)
      }
    })();
  }, []);

  const doRefresh: (event: CustomEvent<RefresherEventDetail>) => void = async (event) => {
    console.log("do refresh");

    //stop the spinner
    try {
      const data = await getCoursesData();
      setCourses(data);
      event.detail.complete();
    } catch (err) {
      //inform the user that a problem occurred (some kind of alert)
    }
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Instructors</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">Tab 3</IonTitle>
          </IonToolbar>
        </IonHeader>
        <ExploreContainer name="Tab 3 page" />
      </IonContent>
    </IonPage>
  );
};

export default Instructors;
