import { IonList, IonItem } from "@ionic/react";
import "./CoursesList.css";
import Course from "../models/Course";

interface CoursesListProps {
  courses: Course[];
  searchText: string;
}

const AcademicYearsList: React.FC<CoursesListProps> = ({ courses, searchText }) => {
  return (
    <IonList>
      {courses
        .filter((course) => {
          return course.Title.toLowerCase().includes(searchText.toLowerCase());
        })
        .map((course, index) => {
          return (
            <IonItem button detail routerLink={`/academicyears/${course.Id}`} key={index}>
              {course.Title}
            </IonItem>
          );
        })}
    </IonList>
  );
};

export default AcademicYearsList;
