import React, { useState, useEffect } from "react";
import { RouteComponentProps } from "react-router";
import {
  IonButtons,
  IonBackButton,
  IonContent,
  IonHeader,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
} from "@ionic/react";
import { RefresherEventDetail } from "@ionic/core";
import "./AcademicYearSemesterCourseDetails.css";
import { PrerequisiteCoursesList, PrerequisitesForList } from "../components/AcademicYearSemesterCourseDetailsList";
import Course from "../models/Course";
import axios from "axios";

interface AcademicYearSemesterCourseDetailsProps
  extends RouteComponentProps<{
    id: string;
  }> {}

const AcademicYearSemesterCourseDetails: React.FC<AcademicYearSemesterCourseDetailsProps> = ({ match }) => {
  const academicYearSemesterPrerequisiteCoursesData: Course[] = [];
  const [academicYearSemesterPrerequisiteCourses, setAcademicYearSemesterPrerequisiteCourses]: [
    Course[],
    (academicYearSemesterPrerequisiteCoursesData: Course[]) => void
  ] = useState(academicYearSemesterPrerequisiteCoursesData);

  const academicYearSemesterPrerequisitesForData: Course[] = [];
  const [academicYearSemesterPrerequisitesFor, setAcademicYearSemesteritePrerequisitesFor]: [
    Course[],
    (academicYearSemesterPrerequisitesForData: Course[]) => void
  ] = useState(academicYearSemesterPrerequisitesForData);

  const getAcademicYearSemesterCourseDetailsData = async () => {
    return new Promise(async (resolve: (value: [Course[], Course[]]) => void, reject) => {
      const response = await axios.get(
        `https://webd3000functions-apim.azure-api.net/webd3000functions/course/${match.params.id}`
      );

      if (response.status === 200) {
        console.debug(response.data);

        resolve([response.data.prerequisiteCourses, response.data.prerequisitesFor]);
      } else {
        reject("Unable to access data");
      }
    });
  };

  useEffect(() => {
    (async () => {
      console.log("use effect");
      try {
        const emptyCourse
        const data = await getAcademicYearSemesterCourseDetailsData();
        if (data[0].length === 0) {
          setAcademicYearSemesterPrerequisiteCourses();
        } else {
          setAcademicYearSemesterPrerequisiteCourses(data[0]);
        }
        setAcademicYearSemesteritePrerequisitesFor(data[1]);
      } catch (err) {
        //inform the user that a problem occurred (some kind of alert)
      }
    })();
  }, []);

  const doRefresh: (event: CustomEvent<RefresherEventDetail>) => void = async (event) => {
    console.log("do refresh");

    //stop the spinner
    try {
      const data = await getAcademicYearSemesterCourseDetailsData();
      setAcademicYearSemesterPrerequisiteCourses(data[0]);
      setAcademicYearSemesteritePrerequisitesFor(data[1]);
      event.detail.complete();
    } catch (err) {
      //inform the user that a problem occurred (some kind of alert)
    }
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton defaultHref="home" />
          </IonButtons>
          <IonTitle>Course Details</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={doRefresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>
        <IonToolbar>
          <IonTitle color="primary">Prerequsite Courses</IonTitle>
          <PrerequisiteCoursesList prerequisiteCourses={academicYearSemesterPrerequisiteCourses} />
        </IonToolbar>
        <IonToolbar>
          <IonTitle color="primary">Prerequisites For</IonTitle>
          <PrerequisitesForList prerequisitesFor={academicYearSemesterPrerequisitesFor} />
        </IonToolbar>
      </IonContent>
    </IonPage>
  );
};

export default AcademicYearSemesterCourseDetails;
